uint8_t plaintext[DEMO_MESSAGE_SIZE];
uint8_t cipher[DEMO_MESSAGE_SIZE];


int qeep_demo(void)
{
	QEEP_RET ret = QEEP_OK;
	
	int32_t message_length = DEMO_MESSAGE_SIZE;
	int32_t cipher_length = 0;

	int32_t qeep_key_len = DEMO_QEEP_KEY_SIZE;

	QP_Handle qp_handle_enc;
	QP_Handle qp_handle_dec;

	PRINTF("QiSpace QEEP Demo Start...\r\n");

	/**
	 * ##################################################################
	 *   Use QEEP key to encode message
	 *  ##################################################################
	 */

	PRINTF("QiSpace QEEP Demo Encode operation starts....\r\n");
	/**
	 *    Step 1, Create QEEP handle
	 */
	ret = QP_init(&qp_handle_enc);
	if (ret != QEEP_OK) {
	    PRINTF("QP_init fail ret = %d\r\n", ret);
		return ret;
	}

	/**
	 *    Step 2, Load QEEP key
	 */
	ret = QP_qeep_key_load_en(qp_handle_enc, qeep_key, qeep_key_len);
	if (ret != QEEP_OK) {
	    PRINTF("QP_qeep_key_load fail ret = %d\n", ret);
		return ret;
	}

	/**
	 *    Step 3, Load IV
	 */
	ret = QP_iv_set(qp_handle_enc, iv_qeep, DEMO_IV_SIZE);
	if (ret != QEEP_OK) {
	    PRINTF("fail at QP_iv_set ret = %d\r\n", ret);
		return ret;	
	}

	/**
	 *    Step 4, Encrypt operation
	 */
	ret = QP_encrypt(qp_handle_enc, message,  message_length, cipher);
	if (ret != QEEP_OK) {
	   PRINTF("fail at QP_encode ret = %d\r\n", ret);
	   return ret;
	}

	cipher_length = message_length;
	/**
	 *    Step 5, Free QEEP handle
	 */
	ret = QP_close(qp_handle_enc);
	PRINTF("QiSpace QEEP Demo Encode operation done ... \n");

	/**
	 *  Verify encrypt resuls for test purpose
	 */
	ret = memcmp(cipher, cipher_exp, message_length);
	PRINTF("QiSpace QEEP Demo Compare cipher and cipher_exp ....\r\n");
	if ( ret == 0 ) {
		PRINTF("Match! \r\n");
	} else {
		PRINTF("NOT match (ret= %d) \n", ret);
	}


	/**
	 * ##################################################################
	 *   Use QEEP key to decode cipher
	 *  ##################################################################
	 */

	PRINTF("QiSpace QEEP Demo Decode operation starts....\r\n");
	/**
	 *    Step 1, Create QEEP handle
	 */
	ret = QP_init(&qp_handle_dec);
	if (ret != QEEP_OK) {
	   PRINTF("QP_init fail ret = %d\r\n", ret);
	   return ret;
	}

	/**
	 *    Step 2, Load QEEP key
	 */
	ret = QP_qeep_key_load_de(qp_handle_dec, qeep_key, qeep_key_len);
	if (ret != QEEP_OK) {
	    PRINTF("QP_qeep_key_load fail ret = %d\r\n", ret);
		return ret;
	}

	/**
	 *    Step 3, Load IV
	 */
	ret = QP_iv_set(qp_handle_dec, iv_qeep, DEMO_IV_SIZE);
	if (ret != QEEP_OK) {
	    PRINTF("QP_iv_set fail ret = %d\r\n", ret);
		return ret;
	}

	/**
	 *    Step 4, Decrypt operation
	 */
	ret = QP_decrypt(qp_handle_dec, cipher,  cipher_length, plaintext);
	if (ret != QEEP_OK) {
	    PRINTF("QP_decrypt fail ret = %d\r\n", ret);
		return ret;
	}

	/**
	 *    Step 5, Free QEEP handle
	 */
	ret = QP_close(qp_handle_dec);
	PRINTF("QiSpace QEEP Demo Decode operation done ... \n");

	/**
	 *  Verify encrypt and decrypt resuls for test purpose
	 */
	ret = memcmp(message, plaintext, message_length);
	PRINTF("QiSpace QEEP Demo Compare message and plaintext ....\r\n");
	if ( ret == 0 ) {
		PRINTF("Match! \r\n");
	} else {
		PRINTF("NOT match (ret= %d) \n", ret);
	}

    PRINTF("QiSpace QEEP Demo end...\r\n");

	return ret;
}


